import numpy as np
import matplotlib.pyplot as plt

"""
画x-y二维图像
"""
class plot_2D_util:
    def __init__(self, x_list, y_list):
        self.x_list = x_list
        self.y_list = y_list
    def single_dashed_plot(self, line_label_str):
        x_arr = np.array(self.x_list)
        y_arr = np.array(self.y_list)

        fig, ax = plt.subplots()
        # Using set_dashes() to modify dashing of an existing line
        line1, = ax.plot(x_arr, y_arr, label=line_label_str)
        # 2pt line, 2pt break, 10pt line, 2pt break
        line1.set_dashes([2, 2, 10, 2])

        ax.legend()
        plt.show()
    def multi_dashed_plots(self, line_label_str_list):
        if type(line_label_str_list) is not list:
            pass
    def single_dot_plot(self,x_label, y_label, line_label_str):
        x_arr = np.array(self.x_list)
        y_arr = np.array(self.y_list)
        fig, ax = plt.subplots(1)
        ax.plot(x_arr, y_arr, 'o--')
        ax.set_xlabel(x_label)
        ax.set_ylabel(y_label)
        # plt.xlabel(x_label)
        # plt.ylabel(y_label)
        # plt.title(line_label_str)
        plt.show()
    def multi_dot_plots(self):
        pass

